import React, { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { supabase } from '../utils/supabaseClient';
import { Calendar } from '../components/Calendar';
import { WeatherWidget } from '../components/WeatherWidget';
import '../styles/main.css';

interface User {
  name: string;
  email: string;
}

interface Outfit {
  id: string;
  name: string;
  occasion: string | null;
  created_at: string;
  items: {
    id: string;
    name: string;
    image_url: string;
  }[];
}

const Dashboard: React.FC = () => {
  const [user, setUser] = useState<User | null>(null);
  const [todaysOutfit, setTodaysOutfit] = useState<Outfit | null>(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      const { data: { user } } = await supabase.auth.getUser();
      if (user) {
        setUser({
          name: user.user_metadata?.full_name || 'User',
          email: user.email || '',
        });
      }
    };

    fetchTodaysOutfit();
    fetchUser();
  }, []);

  const fetchTodaysOutfit = async () => {
    const todayStr = new Date().toISOString().split('T')[0]; // "YYYY-MM-DD"

    const { data, error }: { data: any[] | null, error: any } = await supabase
      .from('outfit_schedules')
      .select(`
        date,
        outfits:outfit_id (
          id,
          name,
          occasion,
          outfit_items (
            clothing_items (
              id,
              name,
              image_url
            )
          )
        )
      `)
      .eq('date', todayStr)
      .limit(1);

    if (error) {
      console.error('Error fetching today’s outfit:', error);
      return;
    }

    const first = data?.[0];
    if (first && first.outfits && !Array.isArray(first.outfits)) {
      const transformed: Outfit = {
        id: first.outfits.id,
        name: first.outfits.name,
        occasion: first.outfits.occasion,
        created_at: first.date,
        items: (first.outfits.outfit_items || []).map((oi: any) => oi.clothing_items)
      };
      setTodaysOutfit(transformed);
    } else {
      console.warn("Unexpected format for today's outfit:", data);
    }
  };  

  const handleClothingScanner = () => {
    navigate('/scanner');
  };

  const handleLogout = async () => {
    try {
      await supabase.auth.signOut();
      navigate('/');
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  return (
    <div className="dashboard-container" style={{ 
      fontFamily: "'Poppins', sans-serif",
      color: "#666666",
      fontSize: "14px",
      lineHeight: "1.80857",
      fontWeight: "normal",
      overflowX: "hidden"
    }}>
      {todaysOutfit && (
        <section className="todays-outfit" style={{
          padding: "30px 20px",
          backgroundColor: "#fff7ed",
          marginBottom: "20px",
          borderRadius: "8px",
          boxShadow: "0 2px 8px rgba(0,0,0,0.06)"
        }}>
          <h2 style={{
            fontSize: "26px",
            fontWeight: "bold",
            marginBottom: "10px",
            color: "#92400e"
          }}>
            Today’s Fit: {todaysOutfit.name}
          </h2>
          {todaysOutfit.occasion && (
            <p style={{
              marginBottom: "15px",
              color: "#b45309",
              fontStyle: "italic"
            }}>
              {todaysOutfit.occasion}
            </p>
          )}
          <div style={{ display: "flex", gap: "10px", flexWrap: "wrap" }}>
          {todaysOutfit.items.map((item: { id: string; name: string; image_url: string }) => (
              <img
                key={item.id}
                src={item.image_url}
                alt={item.name}
                style={{
                  width: "70px",
                  height: "70px",
                  objectFit: "cover",
                  borderRadius: "6px",
                  border: "1px solid #e0e0e0"
                }}
              />
            ))}
          </div>
        </section>
      )}
      <div className="dashboard-header" style={{
        width: "100%",
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        padding: "15px 0",
        maxWidth: "1200px",
        margin: "0 auto",
        backgroundImage: "url(../images/header-bg.png)",
        backgroundSize: "100%",
        backgroundRepeat: "no-repeat"
      }}>
        <div className="header-content">
          <h1 style={{
            fontSize: '2.5rem',
            marginBottom: '1rem',
            color: 'var(--color-green)',
            display: 'flex',
            alignItems: 'center'
          }}>
            <img 
              src="/images/wrdrb_logo.png" 
              alt="wrdrb" 
              style={{
                maxWidth: '150px',
                height: 'auto'
              }}
            />
          </h1>
          <p className="dashboard-subtitle" style={{
            fontSize: "16px",
            color: "#4a4949",
            margin: "0"
          }}>
            Welcome, {user?.email ? user.email.split('@')[0] : 'User'}. What are we wearing today?
          </p>
        </div>
        <button onClick={handleLogout} className="btn btn-secondary logout-btn" style={{
          fontSize: "16px",
          color: "#fefefd",
          backgroundColor: "#d07d6b",
          padding: "10px 20px",
          textTransform: "uppercase",
          fontWeight: "bold",
          borderRadius: "5px",
          border: "none",
          cursor: "pointer",
          whiteSpace: "nowrap"
        }}>
          Log Out
        </button>
      </div>

        {todaysOutfit && (
          <section style={{
            backgroundColor: '#fffbe6',
            padding: '30px',
            borderRadius: '10px',
            margin: '20px',
            boxShadow: '0 4px 12px rgba(0,0,0,0.06)',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'flex-start'
          }}>
            <h2 style={{
              fontSize: '26px',
              color: '#3b3b3b',
              fontWeight: 'bold',
              marginBottom: '12px'
            }}>
              Today’s Outfit
            </h2>
            <p style={{
              fontSize: '18px',
              fontWeight: 500,
              marginBottom: '10px'
            }}>
              {todaysOutfit.name} {todaysOutfit.occasion && `– ${todaysOutfit.occasion}`}
            </p>
            <div style={{ display: 'flex', gap: '12px', flexWrap: 'wrap' }}>
              {todaysOutfit.items.map((item: any) => (
                <img
                  key={item.id}
                  src={item.image_url}
                  alt={item.name}
                  style={{
                    width: '70px',
                    height: '70px',
                    objectFit: 'cover',
                    borderRadius: '8px',
                    border: '1px solid #ddd'
                  }}
                />
              ))}
            </div>
          </section>
        )}
      <div className="dashboard-grid" style={{
        display: "grid",
        gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
        gap: "20px",
        padding: "20px"
      }}>
        <div className="dashboard-card" style={{
          backgroundColor: "#ffffff",
          boxShadow: "0px 0px 20px 0px rgba(0,0,0,0.1)",
          padding: "30px",
          borderRadius: "5px"
        }}>
          <h2 className="card-title" style={{
            fontSize: "22px",
            color: "#3b3b3b",
