import React, { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { supabase } from '../utils/supabaseClient';
import { Calendar } from '../components/Calendar';
import { WeatherWidget } from '../components/WeatherWidget';
import '../styles/main.css';

interface User {
  name: string;
  email: string;
}

interface Outfit {
  id: string;
  name: string;
  occasion: string | null;
  created_at: string;
  items: {
    id: string;
    name: string;
    image_url: string;
  }[];
}

const Dashboard: React.FC = () => {
  const [user, setUser] = useState<User | null>(null);
  const [todaysOutfit, setTodaysOutfit] = useState<Outfit | null>(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      const { data: { user } } = await supabase.auth.getUser();
      if (user) {
        setUser({
          name: user.user_metadata?.full_name || 'User',
          email: user.email || '',
        });
      }
    };

    fetchTodaysOutfit();
    fetchUser();
  }, []);

  const fetchTodaysOutfit = async () => {
    const todayStr = new Date().toISOString().split('T')[0]; // "YYYY-MM-DD"

    const { data, error }: { data: any[] | null, error: any } = await supabase
      .from('outfit_schedules')
      .select(`
        date,
        outfits:outfit_id (
          id,
          name,
          occasion,
          outfit_items (
            clothing_items (
              id,
              name,
              image_url
            )
          )
        )
      `)
      .eq('date', todayStr)
      .limit(1);

    if (error) {
      console.error('Error fetching today’s outfit:', error);
      return;
    }

    const first = data?.[0];
    if (first && first.outfits && !Array.isArray(first.outfits)) {
      const transformed: Outfit = {
        id: first.outfits.id,
        name: first.outfits.name,
        occasion: first.outfits.occasion,
        created_at: first.date,
        items: (first.outfits.outfit_items || []).map((oi: any) => oi.clothing_items)
      };
      setTodaysOutfit(transformed);
    } else {
      console.warn("Unexpected format for today's outfit:", data);
    }
  };  

  const handleClothingScanner = () => {
    navigate('/scanner');
  };

  const handleLogout = async () => {
    try {
      await supabase.auth.signOut();
      navigate('/');
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  return (
    <div className="dashboard-container" style={{ 
      fontFamily: "'Poppins', sans-serif",
      color: "#666666",
      fontSize: "14px",
      lineHeight: "1.80857",
      fontWeight: "normal",
      overflowX: "hidden"
    }}>
      {todaysOutfit && (
        <section className="todays-outfit" style={{
          padding: "30px 20px",
          backgroundColor: "#fff7ed",
          marginBottom: "20px",
          borderRadius: "8px",
          boxShadow: "0 2px 8px rgba(0,0,0,0.06)"
        }}>
          <h2 style={{
            fontSize: "26px",
            fontWeight: "bold",
            marginBottom: "10px",
            color: "#92400e"
          }}>
            Today’s Fit: {todaysOutfit.name}
          </h2>
          {todaysOutfit.occasion && (
            <p style={{
              marginBottom: "15px",
              color: "#b45309",
              fontStyle: "italic"
            }}>
              {todaysOutfit.occasion}
            </p>
          )}
          <div style={{ display: "flex", gap: "10px", flexWrap: "wrap" }}>
          {todaysOutfit.items.map((item: { id: string; name: string; image_url: string }) => (
              <img
                key={item.id}
                src={item.image_url}
                alt={item.name}
                style={{
                  width: "70px",
                  height: "70px",
                  objectFit: "cover",
                  borderRadius: "6px",
                  border: "1px solid #e0e0e0"
                }}
              />
            ))}
