import React, { useState, useEffect } from 'react';
import { supabase } from '../utils/supabaseClient';
import '../styles/main.css';

interface ClothingItem {
  id: string;
  image_url: string;
  name: string | null;
  category: string;
  color: string;
  occasion: string;
}

interface ItemDetails {
  name: string;
  category: string;
  color: string;
  occasion: string;
  customCategory?: string;
  customOccasion?: string;
}

const ClothingDatabase: React.FC = () => {
  const [clothingItems, setClothingItems] = useState<ClothingItem[]>([]);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [isUploading, setIsUploading] = useState(false);
  const [showQuestionnaire, setShowQuestionnaire] = useState(false);
  const [itemDetails, setItemDetails] = useState<ItemDetails>({
    name: '',
    category: '',
    color: '',
    occasion: '',
  });
  const [showCustomCategory, setShowCustomCategory] = useState(false);
  const [showCustomOccasion, setShowCustomOccasion] = useState(false);

  // Add filter states
  const [filters, setFilters] = useState({
    category: '',
    color: '',
    occasion: ''
  });

  const [editingItem, setEditingItem] = useState<ClothingItem | null>(null);
  const [showEditModal, setShowEditModal] = useState(false);

  const categories = [
    'Jacket',
    'Shirt',
    'T-Shirt',
    'Sweater',
    'Dress',
    'Skirt',
    'Pants',
    'Shorts',
    'Jeans',
    'Shoes',
    'Accessories',
    'Other...'
  ];

  const occasions = [
    'Casual',
    'Business Casual',
    'Formal',
    'Business Formal',
    'Sportswear',
    'Beachwear',
    'Party',
    'Other...'
  ];

  const colors = [
    'Black',
    'White',
    'Gray',
    'Navy',
    'Blue',
    'Red',
    'Green',
    'Yellow',
    'Purple',
    'Pink',
    'Brown',
    'Beige',
    'Orange',
    'Other...'
  ];

  useEffect(() => {
    fetchClothingItems();
  }, []);

  const fetchClothingItems = async () => {
    try {
      const { data, error } = await supabase
        .from('clothing_items')
        .select('*');

      if (error) throw error;
      if (data) setClothingItems(data);
    } catch (error) {
      console.error('Error fetching clothing items:', error);
    }
  };

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files[0]) {
      setSelectedFile(event.target.files[0]);
      setShowQuestionnaire(true);
    }
  };

  const handleCategoryChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const value = e.target.value;
    setShowCustomCategory(value === 'Other...');
    setItemDetails(prev => ({
      ...prev,
      category: value === 'Other...' ? '' : value,
      customCategory: value === 'Other...' ? '' : undefined
    }));
  };

  const handleOccasionChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const value = e.target.value;
    setShowCustomOccasion(value === 'Other...');
    setItemDetails(prev => ({
      ...prev,
      occasion: value === 'Other...' ? '' : value,
      customOccasion: value === 'Other...' ? '' : undefined
    }));
  };

  const handleColorChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const value = e.target.value;
    setItemDetails(prev => ({
      ...prev,
      color: value === 'Other...' ? '' : value
    }));
  };

  const handleCustomInputChange = (field: 'category' | 'occasion', value: string) => {
    setItemDetails(prev => ({
      ...prev,
      [field]: value,
      [`custom${field.charAt(0).toUpperCase() + field.slice(1)}`]: value
    }));
  };

  const handleUpload = async () => {
    if (!selectedFile || !itemDetails.category || !itemDetails.color || !itemDetails.occasion) {
      alert('Please fill in all required fields');
      return;
    }

    setIsUploading(true);
    try {
      // Check file size (max 10MB)
      if (selectedFile.size > 10 * 1024 * 1024) {
        throw new Error('File size must be less than 10MB. Please compress your image if it\'s larger.');
      }

      // Check file type
      if (!selectedFile.type.startsWith('image/')) {
        throw new Error('File must be an image');
      }

      const fileExt = selectedFile.name.split('.').pop();
      const fileName = `${Math.random()}.${fileExt}`;
      const filePath = `${fileName}`;

      console.log('Starting upload to Supabase storage...');
      const { error: uploadError } = await supabase.storage
        .from('clothing-images')
        .upload(filePath, selectedFile);

      if (uploadError) {
        console.error('Upload error:', uploadError);
        throw new Error(`Upload failed: ${uploadError.message}`);
      }

      console.log('Getting public URL...');
      const { data: { publicUrl } } = supabase.storage
        .from('clothing-images')
        .getPublicUrl(filePath);

      console.log('Inserting record into database...');
      const { data: insertData, error: insertError } = await supabase
        .from('clothing_items')
        .insert([
          {
            image_url: publicUrl,
            name: itemDetails.name || null,
            category: itemDetails.category,
            color: itemDetails.color,
            occasion: itemDetails.occasion
          },
        ])
        .select();

      if (insertError) {
        console.error('Database insert error details:', {
          error: insertError,
          code: insertError.code,
          message: insertError.message,
          details: insertError.details,
          hint: insertError.hint
        });
        throw new Error(`Database insert failed: ${insertError.message || 'Unknown error'}`);
      }

      console.log('Insert successful:', insertData);
      setSelectedFile(null);
      setShowQuestionnaire(false);
      setItemDetails({
        name: '',
        category: '',
        color: '',
        occasion: '',
      });
      fetchClothingItems();
    } catch (error) {
      console.error('Error uploading file:', error);
      alert(error instanceof Error ? error.message : 'Error uploading file');
    } finally {
      setIsUploading(false);
    }
  };

  // Add filter change handlers
  const handleFilterChange = (field: 'category' | 'color' | 'occasion', value: string) => {
    setFilters(prev => ({
      ...prev,
      [field]: value
    }));
  };

  // Add filtered items computation
  const filteredItems = clothingItems.filter(item => {
    return (
      (!filters.category || item.category === filters.category) &&
      (!filters.color || item.color === filters.color) &&
      (!filters.occasion || item.occasion === filters.occasion)
    );
  });

  const handleEdit = (item: ClothingItem) => {
    setEditingItem(item);
    setItemDetails({
      name: item.name || '',
      category: item.category,
      color: item.color,
      occasion: item.occasion
    });
    setShowEditModal(true);
  };

  const handleDelete = async (item: ClothingItem) => {
    if (window.confirm('Are you sure you want to delete this item?')) {
      try {
        // Delete from storage
        const filePath = item.image_url.split('/').pop();
        if (filePath) {
          const { error: storageError } = await supabase.storage
            .from('clothing-images')
            .remove([filePath]);

          if (storageError) throw storageError;
        }

        // Delete from database
        const { error: dbError } = await supabase
          .from('clothing_items')
          .delete()
          .eq('id', item.id);

        if (dbError) throw dbError;

        // Update UI
        setClothingItems(items => items.filter(i => i.id !== item.id));
      } catch (error) {
        console.error('Error deleting item:', error);
        alert('Error deleting item');
      }
    }
  };

  const handleUpdate = async () => {
    if (!editingItem) return;

    try {
      const { error } = await supabase
        .from('clothing_items')
        .update({
          name: itemDetails.name || null,
          category: itemDetails.category,
          color: itemDetails.color,
          occasion: itemDetails.occasion
        })
        .eq('id', editingItem.id);

      if (error) throw error;

      // Update UI
      setClothingItems(items =>
        items.map(item =>
          item.id === editingItem.id
            ? {
                ...item,
                name: itemDetails.name || null,
                category: itemDetails.category,
                color: itemDetails.color,
                occasion: itemDetails.occasion
              }
            : item
        )
      );

      setShowEditModal(false);
      setEditingItem(null);
      setItemDetails({
        name: '',
        category: '',
        color: '',
        occasion: '',
      });
    } catch (error) {
      console.error('Error updating item:', error);
      alert('Error updating item');
    }
  };

  return (
    <div className="dashboard">
      <h1 className="dashboard-title">Clothing Database</h1>

      <div className="card" style={{ marginBottom: '2rem' }}>
        <h2 className="section-title">Add New Item</h2>
        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          <input
            type="file"
            accept="image/*"
            onChange={handleFileSelect}
            style={{
              padding: '0.5rem',
              borderRadius: '9999px',
              fontSize: '0.875rem',
              backgroundColor: '#f3f4f6',
              color: '#1f2937',
              border: 'none',
              cursor: 'pointer',
            }}
          />
        </div>
      </div>

      {/* Filters */}
      <div className="card" style={{ marginBottom: '2rem' }}>
        <h2 className="section-title">Filters</h2>
        <div className="filters-grid">
          <div className="form-group">
            <label>Category:</label>
            <select
              value={filters.category}
              onChange={(e) => handleFilterChange('category', e.target.value)}
              className="form-select"
            >
              <option value="">All Categories</option>
              {categories.map(category => (
                <option key={category} value={category}>{category}</option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label>Color:</label>
            <select
              value={filters.color}
              onChange={(e) => handleFilterChange('color', e.target.value)}
              className="form-select"
            >
              <option value="">All Colors</option>
              {colors.map(color => (
                <option key={color} value={color}>{color}</option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label>Occasion:</label>
            <select
              value={filters.occasion}
              onChange={(e) => handleFilterChange('occasion', e.target.value)}
              className="form-select"
            >
              <option value="">All Occasions</option>
              {occasions.map(occasion => (
                <option key={occasion} value={occasion}>{occasion}</option>
              ))}
            </select>
          </div>
        </div>
      </div>

      {/* Questionnaire Modal */}
      {showQuestionnaire && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2 className="section-title">Item Details</h2>
            <div className="form-group">
              <label>Name (optional):</label>
              <input
                type="text"
                placeholder="Give your item a name"
                value={itemDetails.name}
                onChange={(e) => setItemDetails(prev => ({ ...prev, name: e.target.value }))}
                className="form-input"
              />
            </div>

            <div className="form-group">
              <label>Category:</label>
              <select 
                value={itemDetails.category} 
                onChange={handleCategoryChange}
                className="form-select"
              >
                <option value="">Select a category</option>
                {categories.map(category => (
                  <option key={category} value={category}>{category}</option>
                ))}
              </select>
              {showCustomCategory && (
                <input
                  type="text"
                  placeholder="Enter custom category"
                  value={itemDetails.customCategory || ''}
                  onChange={(e) => handleCustomInputChange('category', e.target.value)}
                  className="form-input"
                />
              )}
            </div>

            <div className="form-group">
              <label>Color:</label>
              <select 
                value={itemDetails.color} 
                onChange={handleColorChange}
                className="form-select"
              >
                <option value="">Select a color</option>
                {colors.map(color => (
                  <option key={color} value={color}>{color}</option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>Occasion:</label>
              <select 
                value={itemDetails.occasion} 
                onChange={handleOccasionChange}
                className="form-select"
              >
                <option value="">Select an occasion</option>
                {occasions.map(occasion => (
                  <option key={occasion} value={occasion}>{occasion}</option>
                ))}
              </select>
              {showCustomOccasion && (
                <input
                  type="text"
                  placeholder="Enter custom occasion"
                  value={itemDetails.customOccasion || ''}
                  onChange={(e) => handleCustomInputChange('occasion', e.target.value)}
                  className="form-input"
                />
              )}
            </div>

            <div className="modal-actions">
              <button 
                onClick={() => {
                  setShowQuestionnaire(false);
                  setSelectedFile(null);
                  setItemDetails({
                    name: '',
                    category: '',
                    color: '',
                    occasion: '',
                  });
                }}
                className="button button-secondary"
              >
                Cancel
              </button>
              <button
                onClick={handleUpload}
                disabled={!itemDetails.category || !itemDetails.color || !itemDetails.occasion || isUploading}
                className="button"
              >
                {isUploading ? 'Uploading...' : 'Upload'}
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Clothing Items Grid */}
      <div className="clothing-grid">
        {filteredItems.map((item) => (
          <div key={item.id} className="clothing-item">
            <div className="clothing-image-container">
              <img
                src={item.image_url}
                alt={item.name || item.category}
                className="clothing-image"
              />
            </div>
            <div className="clothing-details">
              <h3>{item.name || item.category}</h3>
              <p>Color: {item.color}</p>
              <p>Occasion: {item.occasion}</p>
              <div className="clothing-actions">
                <button
                  onClick={() => handleEdit(item)}
                  className="button button-secondary"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(item)}
                  className="button button-danger"
                >
                  Delete
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Edit Modal */}
      {showEditModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2 className="section-title">Edit Item</h2>
            <div className="form-group">
              <label>Name (optional):</label>
              <input
                type="text"
                placeholder="Give your item a name"
                value={itemDetails.name}
                onChange={(e) => setItemDetails(prev => ({ ...prev, name: e.target.value }))}
                className="form-input"
              />
            </div>

            <div className="form-group">
              <label>Category:</label>
              <select
                value={itemDetails.category}
                onChange={handleCategoryChange}
                className="form-select"
              >
                <option value="">Select a category</option>
                {categories.map(category => (
                  <option key={category} value={category}>{category}</option>
                ))}
              </select>
              {showCustomCategory && (
                <input
                  type="text"
                  placeholder="Enter custom category"
                  value={itemDetails.customCategory || ''}
                  onChange={(e) => handleCustomInputChange('category', e.target.value)}
                  className="form-input"
                />
              )}
            </div>

            <div className="form-group">
              <label>Color:</label>
              <select
                value={itemDetails.color}
                onChange={handleColorChange}
                className="form-select"
              >
                <option value="">Select a color</option>
                {colors.map(color => (
                  <option key={color} value={color}>{color}</option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>Occasion:</label>
              <select
                value={itemDetails.occasion}
                onChange={handleOccasionChange}
                className="form-select"
              >
                <option value="">Select an occasion</option>
                {occasions.map(occasion => (
                  <option key={occasion} value={occasion}>{occasion}</option>
                ))}
              </select>
              {showCustomOccasion && (
                <input
                  type="text"
                  placeholder="Enter custom occasion"
                  value={itemDetails.customOccasion || ''}
                  onChange={(e) => handleCustomInputChange('occasion', e.target.value)}
                  className="form-input"
                />
              )}
            </div>

            <div className="modal-actions">
              <button
                onClick={() => {
                  setShowEditModal(false);
                  setEditingItem(null);
                  setItemDetails({
                    category: '',
                    color: '',
                    occasion: ''
                  });
                }}
                className="button button-secondary"
              >
                Cancel
              </button>
              <button
                onClick={handleUpdate}
                disabled={!itemDetails.category || !itemDetails.color || !itemDetails.occasion}
                className="button"
              >
                Update
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ClothingDatabase;
