  const fetchWeatherData = async () => {
    try {
      setWeatherLoading(true);
      setWeatherError(null);

      const apiKey = process.env.REACT_APP_OPENWEATHER_API_KEY;

      if (!apiKey) {
        throw new Error('OpenWeather API key is not configured');
      }

      const city = 'Toronto'; // You can make this dynamic based on user location

      // Fetch 5-day forecast
      const forecastResponse = await fetch(
        `https://api.openweathermap.org/data/2.5/forecast?q=${city}&appid=${apiKey}&units=metric`
      );

      if (!forecastResponse.ok) {
        throw new Error('Failed to fetch forecast data');
      }

      const forecastData = await forecastResponse.json();

      // Process forecast data to get one reading per day at noon
      const dailyForecasts = forecastData.list
        .filter((item: any, index: number) => index % 8 === 0) // Get one reading per day (every 8th item as data is in 3-hour intervals)
        .map((item: any) => ({
          date: new Date(item.dt * 1000).toISOString().split('T')[0], // Format as YYYY-MM-DD
          temperature: Math.round(item.main.temp),
          condition: item.weather[0].main,
          icon: `http://openweathermap.org/img/wn/${item.weather[0].icon}.png`
        }))
        .slice(0, 5); // Get only 5 days

      console.log('Fetched weather data:', dailyForecasts);
      setWeatherData(dailyForecasts);
    } catch (error) {
      console.error('Error fetching weather:', error);
      setWeatherError(error instanceof Error ? error.message : 'Failed to load weather data');
