import React, { useEffect, useState } from 'react';
import { format } from 'date-fns';
import '../styles/main.css';

interface WeatherData {
  temperature: number;
  condition: string;
  icon: string;
}

export const WeatherWidget: React.FC = () => {
  const [weather, setWeather] = useState<WeatherData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        const apiKey = process.env.REACT_APP_OPENWEATHER_API_KEY;

        if (!apiKey) {
          throw new Error('OpenWeather API key is not configured');
        }

        const city = 'Toronto'; // You can make this dynamic based on user location
        const response = await fetch(
          `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`
        );

        if (!response.ok) {
          throw new Error('Failed to fetch weather data');
        }

        const data = await response.json();

        setWeather({
          temperature: Math.round(data.main.temp),
          condition: data.weather[0].main,
          icon: `http://openweathermap.org/img/wn/${data.weather[0].icon}@2x.png`
        });
      } catch (error) {
        console.error('Error fetching weather:', error);
        setError(error instanceof Error ? error.message : 'Failed to load weather data');
      } finally {
        setLoading(false);
      }
    };

    fetchWeather();
  }, []);

  if (loading) {
    return (
      <div className="weather-widget">
        <div className="weather-info">
          <div className="spinner" style={{ marginRight: '0.5rem' }}></div>
          Loading weather...
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="weather-widget">
        <div className="weather-info">
          <p style={{ color: '#991b1b' }}>Error: {error}</p>
          <p style={{ fontSize: '0.875rem', color: '#6b7280' }}>
            Please check your OpenWeather API key configuration
          </p>
        </div>
      </div>
    );
  }

  if (!weather) {
    return (
      <div className="weather-widget">
        <div className="weather-info">
          <p style={{ color: '#991b1b' }}>Unable to load weather data</p>
          <p style={{ fontSize: '0.875rem', color: '#6b7280' }}>
            Please check your OpenWeather API key configuration
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="weather-widget">
      <div className="weather-info">
        <img src={weather.icon} alt={weather.condition} className="weather-icon" />
        <div className="weather-details">
          <p className="temperature">{weather.temperature}Â°C</p>
          <p className="condition">{weather.condition}</p>
        </div>
      </div>
    </div>
  );
};
