import React, { useState, useEffect } from 'react';
import { supabase } from '../utils/supabaseClient';
import { useNavigate } from 'react-router-dom';
import '../styles/main.css';

interface ClothingItem {
  id: string;
  image_url: string;
  name: string | null;
  brand: string | null;
  category: string;
  color: string;
  occasion: string;
}

interface Outfit {
  id: string;
  name: string;
  occasion: string | null;
  items: ClothingItem[];
  created_at: string;
}

interface OutfitSchedule {
  id: string;
  outfit_id: string;
  date: string;
  user_id: string;
  created_at: string;
  outfit?: Outfit;
  google_event_id?: string;
}

const OutfitPlanner: React.FC = () => {
  const navigate = useNavigate();
  const [clothingItems, setClothingItems] = useState<ClothingItem[]>([]);
  const [selectedItems, setSelectedItems] = useState<ClothingItem[]>([]);
  const [savedOutfits, setSavedOutfits] = useState<Outfit[]>([]);
  const [showSaveModal, setShowSaveModal] = useState(false);
  const [outfitName, setOutfitName] = useState('');
  const [outfitOccasion, setOutfitOccasion] = useState('');
  const [selectedCategory, setSelectedCategory] = useState<string>('');
  const [scheduledOutfits, setScheduledOutfits] = useState<OutfitSchedule[]>([]);
  const [showScheduleModal, setShowScheduleModal] = useState(false);
  const [selectedOutfitForSchedule, setSelectedOutfitForSchedule] = useState<Outfit | null>(null);
  const [scheduleDate, setScheduleDate] = useState('');
  const [isGoogleCalendarConnected, setIsGoogleCalendarConnected] = useState(false);
  const [activeTab, setActiveTab] = useState<'planner' | 'calendar'>('planner');
  const [calendarEvents, setCalendarEvents] = useState<GoogleCalendarEvent[]>([]);
  const [showCalendarView, setShowCalendarView] = useState(false);

  const categories = [
    'Jacket',
    'Shirt',
    'T-Shirt',
    'Sweater',
    'Dress',
    'Skirt',
    'Pants',
    'Shorts',
    'Jeans',
    'Shoes',
    'Accessories',
  ];

  const categoryOrder = [
    'Headwear',
    'Jacket',
    'Shirt',
    'T-Shirt',
    'Sweater',
    'Dress',
    'Skirt',
    'Pants',
    'Shorts',
    'Jeans',
    'Shoes',
    'Accessories'
  ];

  useEffect(() => {
    fetchClothingItems();
    fetchSavedOutfits();
    checkOutfitSchedulesTable();
    fetchScheduledOutfits();
    checkGoogleCalendarConnection();
    checkUserSettingsTable();
  }, []);

  const fetchClothingItems = async () => {
    try {
      // Get the current user's ID
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) {
        throw new Error('No authenticated user found');
      }

      const { data, error } = await supabase
        .from('clothing_items')
        .select('*')
        .eq('user_id', user.id);

      if (error) throw error;
      if (data) setClothingItems(data);
    } catch (error) {
      console.error('Error fetching clothing items:', error);
    }
  };

  const fetchSavedOutfits = async () => {
    try {
      // Get the current user's ID
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) {
        throw new Error('No authenticated user found');
      }

      const { data, error } = await supabase
        .from('outfits')
        .select('*')
        .eq('user_id', user.id)
        .order('created_at', { ascending: false });

      if (error) throw error;
      if (data) {
        // Fetch the actual clothing items for each outfit
        const outfitsWithItems = await Promise.all(
          data.map(async (outfit) => {
            const { data: items } = await supabase
              .from('clothing_items')
              .select('*')
              .in('id', outfit.items);
            return {
              ...outfit,
              items: items || [],
            };
          })
        );
        setSavedOutfits(outfitsWithItems);
      }
    } catch (error) {
      console.error('Error fetching saved outfits:', error);
    }
  };

  const fetchScheduledOutfits = async () => {
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) return;

      // First, fetch the scheduled outfits
      const { data: schedules, error: schedulesError } = await supabase
        .from('outfit_schedules')
        .select('*')
        .eq('user_id', user.id)
        .order('date', { ascending: true });

      if (schedulesError) throw schedulesError;

      if (schedules) {
        // Then, fetch the outfits for these schedules
        const outfitIds = schedules.map(schedule => schedule.outfit_id);
        const { data: outfits, error: outfitsError } = await supabase
          .from('outfits')
          .select('*, items:clothing_items(*)')
          .in('id', outfitIds);

        if (outfitsError) throw outfitsError;

        // Combine the data
        const transformedSchedules = schedules.map(schedule => ({
          ...schedule,
          outfit: outfits?.find(outfit => outfit.id === schedule.outfit_id)
        }));

        setScheduledOutfits(transformedSchedules);
      }
    } catch (error) {
      console.error('Error fetching scheduled outfits:', error);
    }
  };

  const checkOutfitSchedulesTable = async () => {
    try {
      // Try to query the table to see if it exists
      const { error } = await supabase
        .from('outfit_schedules')
        .select('id')
        .limit(1);

      if (error) {
        console.error('Error checking outfit_schedules table:', error);

        // If the error indicates the table doesn't exist, create it
        if (error.code === '42P01') { // PostgreSQL error code for undefined_table
          console.log('Creating outfit_schedules table...');
          await createOutfitSchedulesTable();
        }
      } else {
        console.log('outfit_schedules table exists');
      }
    } catch (error) {
      console.error('Error in checkOutfitSchedulesTable:', error);
    }
  };

  const createOutfitSchedulesTable = async () => {
    try {
      // This is a simplified approach - in a real app, you'd use migrations
      // or have the table created by your backend
      console.log('This feature requires the outfit_schedules table to be created in Supabase.');
      console.log('Please run the SQL in the outfit_schedules.sql file in your Supabase SQL editor.');

      // Show a more user-friendly message
      alert('The outfit scheduling feature requires database setup. Please contact the administrator.');
    } catch (error) {
      console.error('Error creating outfit_schedules table:', error);
    }
  };

  const handleRandomize = () => {
    const newSelectedItems: ClothingItem[] = [];
    const usedCategories = new Set<string>();

    // Shuffle the clothing items
    const shuffledItems = [...clothingItems].sort(() => Math.random() - 0.5);

    // Select one item from each category
    for (const item of shuffledItems) {
      if (!usedCategories.has(item.category)) {
        newSelectedItems.push(item);
        usedCategories.add(item.category);
      }
    }

    setSelectedItems(newSelectedItems);
  };

  const handleItemSelect = (item: ClothingItem) => {
    // Remove any existing item of the same category
    const filteredItems = selectedItems.filter(i => i.category !== item.category);
    setSelectedItems([...filteredItems, item]);
  };

  const handleSaveOutfit = async () => {
    if (!outfitName || selectedItems.length === 0) {
      alert('Please provide a name for the outfit and select at least one item');
      return;
    }

    try {
      // Get the current user's ID
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) {
        throw new Error('No authenticated user found');
      }

      console.log('Saving outfit with data:', {
        user_id: user.id,
        name: outfitName,
        occasion: outfitOccasion || null,
        items: selectedItems.map(item => item.id)
      });

      const { data, error } = await supabase
        .from('outfits')
        .insert([
          {
            user_id: user.id,
            name: outfitName,
            occasion: outfitOccasion || null,
            items: selectedItems.map(item => item.id),
          },
        ])
        .select();

      if (error) {
        console.error('Supabase error details:', error);
        throw error;
      }

      console.log('Successfully saved outfit:', data);

      setShowSaveModal(false);
      setOutfitName('');
      setOutfitOccasion('');
      fetchSavedOutfits();
    } catch (error) {
      console.error('Error saving outfit:', error);
      if (error instanceof Error) {
        alert(`Error saving outfit: ${error.message}`);
      } else {
        alert('Error saving outfit. Please check the console for details.');
      }
    }
  };

  const handleDeleteOutfit = async (outfitId: string) => {
    if (window.confirm('Are you sure you want to delete this outfit?')) {
      try {
        const { error } = await supabase
          .from('outfits')
          .delete()
          .eq('id', outfitId);

        if (error) throw error;
        fetchSavedOutfits();
      } catch (error) {
        console.error('Error deleting outfit:', error);
        alert('Error deleting outfit');
      }
    }
  };

  const checkGoogleCalendarConnection = async () => {
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) return;

      // Check if user has Google Calendar connected
      const { data: settings } = await supabase
        .from('user_settings')
        .select('google_calendar_connected')
        .eq('user_id', user.id)
        .single();

      setIsGoogleCalendarConnected(settings?.google_calendar_connected || false);
    } catch (error) {
      console.error('Error checking Google Calendar connection:', error);
    }
  };

  const connectGoogleCalendar = async () => {
    try {
      console.log('Starting Google Calendar connection process...');

      const { data: { user } } = await supabase.auth.getUser();
      if (!user) throw new Error('No authenticated user found');
      console.log('User authenticated:', user.id);

      // Check if gapi is loaded
      if (typeof window.gapi === 'undefined') {
        console.error('Google API (gapi) is not loaded. Make sure the script is included in index.html');
        throw new Error('Google API not loaded. Please refresh the page and try again.');
      }

      console.log('Initializing Google Calendar API with:', {
        apiKey: process.env.REACT_APP_GOOGLE_API_KEY ? 'API key exists' : 'API key missing',
        clientId: process.env.REACT_APP_GOOGLE_CLIENT_ID ? 'Client ID exists' : 'Client ID missing'
      });

      // Wait for the Google API to be fully loaded
      await new Promise((resolve, reject) => {
        (window.gapi as any).load('client:auth2', {
          callback: resolve,
          onerror: reject
        });
      });
      console.log('Google API loaded successfully');

      // Initialize Google Calendar API
      try {
        await (window.gapi as any).client.init({
          apiKey: process.env.REACT_APP_GOOGLE_API_KEY as string,
          clientId: process.env.REACT_APP_GOOGLE_CLIENT_ID as string,
          discoveryDocs: ['https://www.googleapis.com/discovery/v1/apis/calendar/v3/rest'],
          scope: 'https://www.googleapis.com/auth/calendar.events',
        });
        console.log('Google API initialized successfully');
      } catch (initError) {
        console.error('Error initializing Google API:', initError);
        throw new Error(`Failed to initialize Google API: ${initError instanceof Error ? initError.message : 'Unknown error'}`);
      }

      // Sign in with Google
      try {
        console.log('Attempting to sign in with Google...');
        const googleUser = await (window.gapi as any).auth2.getAuthInstance().signIn();
        console.log('Google sign-in successful');

        // Update user settings in Supabase
        const { error: settingsError } = await supabase
          .from('user_settings')
          .upsert({
            user_id: user.id,
            google_calendar_connected: true,
            google_access_token: googleUser.getAuthResponse().access_token,
          });

        if (settingsError) {
          console.error('Error updating user settings:', settingsError);
          throw new Error(`Failed to update user settings: ${settingsError.message}`);
        }
        console.log('User settings updated successfully');

        setIsGoogleCalendarConnected(true);
        fetchCalendarEvents();
      } catch (signInError) {
        console.error('Error during Google sign-in:', signInError);
        throw new Error(`Google sign-in failed: ${signInError instanceof Error ? signInError.message : 'Unknown error'}`);
      }
    } catch (error) {
      console.error('Error connecting to Google Calendar:', error);
      alert(`Failed to connect to Google Calendar: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  };

  const fetchCalendarEvents = async () => {
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) return;

      // Get user's Google Calendar settings
      const { data: settings } = await supabase
        .from('user_settings')
        .select('google_access_token')
        .eq('user_id', user.id)
        .single();

      if (!settings?.google_access_token) return;

      // Fetch events from Google Calendar
      const response = await window.gapi.client.calendar.events.list({
        'calendarId': 'primary',
        'timeMin': new Date().toISOString(),
        'showDeleted': false,
        'singleEvents': true,
        'maxResults': 10,
        'orderBy': 'startTime',
      });

      const events = response.result.items;
      setCalendarEvents(events);

      // Update outfit schedules with Google Calendar event IDs
      const scheduledOutfitsWithEvents = await Promise.all(
        scheduledOutfits.map(async (schedule) => {
          const matchingEvent = events.find((event: GoogleCalendarEvent) => 
            event.description?.includes(`outfit_id:${schedule.outfit_id}`)
          );

          if (matchingEvent && !schedule.google_event_id) {
            await supabase
              .from('outfit_schedules')
              .update({ google_event_id: matchingEvent.id })
              .eq('id', schedule.id);
          }

          return {
            ...schedule,
            google_event_id: matchingEvent?.id || schedule.google_event_id,
          };
        })
      );

      setScheduledOutfits(scheduledOutfitsWithEvents);
    } catch (error) {
      console.error('Error fetching calendar events:', error);
    }
  };

  const handleScheduleOutfit = async () => {
    if (!selectedOutfitForSchedule || !scheduleDate) {
      alert('Please select an outfit and a date');
      return;
    }

    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) {
        throw new Error('No authenticated user found');
      }

      // Create outfit schedule in Supabase
      const { data: scheduleData, error: scheduleError } = await supabase
        .from('outfit_schedules')
        .insert([
          {
            user_id: user.id,
            outfit_id: selectedOutfitForSchedule.id,
            date: scheduleDate,
          },
        ])
        .select()
        .single();

      if (scheduleError) throw scheduleError;

      // If Google Calendar is connected, create calendar event
      if (isGoogleCalendarConnected) {
        const event = {
          'summary': `Outfit: ${selectedOutfitForSchedule.name}`,
          'description': `outfit_id:${selectedOutfitForSchedule.id}`,
          'start': {
            'dateTime': `${scheduleDate}T09:00:00`,
            'timeZone': Intl.DateTimeFormat().resolvedOptions().timeZone,
          },
          'end': {
            'dateTime': `${scheduleDate}T17:00:00`,
            'timeZone': Intl.DateTimeFormat().resolvedOptions().timeZone,
          },
        };

        const response = await window.gapi.client.calendar.events.insert({
          'calendarId': 'primary',
          'resource': event,
        });

        // Update outfit schedule with Google Calendar event ID
        await supabase
          .from('outfit_schedules')
          .update({ google_event_id: response.result.id })
          .eq('id', scheduleData.id);
      }

      setShowScheduleModal(false);
      setSelectedOutfitForSchedule(null);
      setScheduleDate('');
      fetchScheduledOutfits();
      if (isGoogleCalendarConnected) {
        fetchCalendarEvents();
      }
    } catch (error) {
      console.error('Error scheduling outfit:', error);
      alert('Error scheduling outfit. Please try again.');
    }
  };

  const handleDeleteSchedule = async (scheduleId: string) => {
    if (window.confirm('Are you sure you want to remove this outfit from your schedule?')) {
      try {
        const { error } = await supabase
          .from('outfit_schedules')
          .delete()
          .eq('id', scheduleId);

        if (error) throw error;
        fetchScheduledOutfits();
      } catch (error) {
        console.error('Error deleting scheduled outfit:', error);
        alert('Error removing outfit from schedule');
      }
    }
  };

  const getSortedItems = (items: ClothingItem[]) => {
    return [...items].sort((a, b) => {
      const indexA = categoryOrder.indexOf(a.category);
      const indexB = categoryOrder.indexOf(b.category);
      return indexA - indexB;
    });
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { 
      weekday: 'long', 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric' 
    });
  };

  const checkUserSettingsTable = async () => {
    try {
      console.log('Checking if user_settings table exists...');

      // Try to query the table to see if it exists
      const { error } = await supabase
        .from('user_settings')
        .select('id')
        .limit(1);

      if (error) {
        console.error('Error checking user_settings table:', error);

        // If the error indicates the table doesn't exist, create it
        if (error.code === '42P01') { // PostgreSQL error code for undefined_table
          console.log('user_settings table does not exist. Please run the SQL in the user_settings.sql file in your Supabase SQL editor.');
          alert('The Google Calendar integration requires the user_settings table to be created in your Supabase database. Please run the SQL in the user_settings.sql file in your Supabase SQL editor.');
        }
      } else {
        console.log('user_settings table exists');
      }
    } catch (error) {
      console.error('Error in checkUserSettingsTable:', error);
    }
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <div className="header-content">
          <h1 className="dashboard-title">Outfit Planner</h1>
        </div>
        <button onClick={() => navigate('/')} className="button button-secondary">
          Back to Dashboard
        </button>
      </div>

      <div className="outfit-planner-tabs">
        <button 
          className={`tab-button ${activeTab === 'planner' ? 'active' : ''}`}
          onClick={() => setActiveTab('planner')}
        >
          Outfit Planner
        </button>
        <button 
          className={`tab-button ${activeTab === 'calendar' ? 'active' : ''}`}
          onClick={() => setActiveTab('calendar')}
        >
          Outfit Calendar
        </button>
      </div>

      {activeTab === 'planner' ? (
        <div className="outfit-planner-container">
          <div className="outfit-preview-section">
            <h2 className="section-title">Current Outfit</h2>
            <div className="outfit-preview-grid">
              {getSortedItems(selectedItems).map((item) => (
                <div key={item.id} className="outfit-preview-item">
                  <img
                    src={item.image_url}
                    alt={item.name || item.category}
                    className="outfit-preview-image"
                  />
                  <div className="outfit-preview-details">
                    <h3>{item.name || item.category}</h3>
                    {item.brand && <p className="item-brand">{item.brand}</p>}
                  </div>
                </div>
              ))}
            </div>
            <div className="outfit-actions">
              <button onClick={handleRandomize} className="button">
                Randomize Outfit
              </button>
              <button
                onClick={() => setShowSaveModal(true)}
                disabled={selectedItems.length === 0}
                className="button"
              >
                Save Outfit
              </button>
            </div>
          </div>

          <div className="clothing-selection-section">
            <h2 className="section-title">Select Items</h2>
            <div className="category-filters">
              <select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="form-select"
              >
                <option value="">All Categories</option>
                {categories.map(category => (
                  <option key={category} value={category}>{category}</option>
                ))}
              </select>
            </div>
            <div className="clothing-selection-grid">
              {clothingItems
                .filter(item => !selectedCategory || item.category === selectedCategory)
                .map((item) => (
                  <div
                    key={item.id}
                    className={`clothing-selection-item ${
                      selectedItems.some(i => i.id === item.id) ? 'selected' : ''
                    }`}
                    onClick={() => handleItemSelect(item)}
                  >
                    <img
                      src={item.image_url}
                      alt={item.name || item.category}
                      className="clothing-selection-image"
                    />
                    <div className="clothing-selection-details">
                      <h3>{item.name || item.category}</h3>
                      {item.brand && <p className="item-brand">{item.brand}</p>}
                    </div>
                  </div>
                ))}
            </div>
          </div>

          <div className="saved-outfits-section">
            <h2 className="section-title">Saved Outfits</h2>
            <div className="saved-outfits-grid">
              {savedOutfits.map((outfit) => (
                <div key={outfit.id} className="saved-outfit-card">
                  <h3>{outfit.name}</h3>
                  {outfit.occasion && <p className="outfit-occasion">{outfit.occasion}</p>}
                  <div className="saved-outfit-preview">
                    {getSortedItems(outfit.items).map((item) => (
                      <img
                        key={item.id}
                        src={item.image_url}
                        alt={item.name || item.category}
                        className="saved-outfit-item-image"
                      />
                    ))}
                  </div>
                  <div className="saved-outfit-actions">
                    <button
                      onClick={() => {
                        setSelectedItems(outfit.items);
                        window.scrollTo({ top: 0, behavior: 'smooth' });
                      }}
                      className="button button-secondary"
                    >
                      Load
                    </button>
                    <button
                      onClick={() => {
                        setSelectedOutfitForSchedule(outfit);
                        setShowScheduleModal(true);
                      }}
                      className="button button-secondary"
                    >
                      Schedule
                    </button>
                    <button
                      onClick={() => handleDeleteOutfit(outfit.id)}
                      className="button button-danger"
                    >
                      Delete
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      ) : (
        <div className="outfit-calendar-container">
          <div className="calendar-header">
            <h2 className="section-title">Outfit Calendar</h2>
            {!isGoogleCalendarConnected && (
              <button onClick={connectGoogleCalendar} className="button button-secondary">
                Connect Google Calendar
              </button>
            )}
            <div className="calendar-view-toggle">
              <button 
                className={`button button-secondary ${!showCalendarView ? 'active' : ''}`}
                onClick={() => setShowCalendarView(false)}
              >
                List View
              </button>
              <button 
                className={`button button-secondary ${showCalendarView ? 'active' : ''}`}
                onClick={() => setShowCalendarView(true)}
              >
                Calendar View
              </button>
            </div>
          </div>

          {showCalendarView ? (
            <div className="calendar-view">
              {calendarEvents.map((event) => {
                const outfitSchedule = scheduledOutfits.find(
                  schedule => schedule.google_event_id === event.id
                );
                const outfit = outfitSchedule?.outfit;

                return (
                  <div key={event.id} className="calendar-event">
                    <div className="event-time">
                      {new Date(event.start.dateTime).toLocaleTimeString([], { 
                        hour: '2-digit', 
                        minute: '2-digit' 
                      })}
                    </div>
                    <div className="event-content">
                      <h3>{event.summary}</h3>
                      {outfit && (
                        <div className="event-outfit-preview">
                          {getSortedItems(outfit.items).map((item) => (
                            <img
                              key={item.id}
                              src={item.image_url}
                              alt={item.name || item.category}
                              className="event-outfit-item-image"
                            />
                          ))}
                        </div>
                      )}
                    </div>
                  </div>
                );
              })}
            </div>
          ) : (
            <div className="scheduled-outfits-list">
              {scheduledOutfits.length === 0 ? (
                <div className="no-items-message">
                  <p>No outfits scheduled yet. Schedule an outfit from the Outfit Planner tab.</p>
                  <div className="setup-instructions">
                    <h3>Database Setup Required</h3>
                    <p>To use the outfit scheduling feature, you need to create the <code>outfit_schedules</code> table in your Supabase database.</p>
                    <ol>
                      <li>Go to your Supabase dashboard</li>
                      <li>Open the SQL Editor</li>
                      <li>Copy and paste the SQL from the <code>outfit_schedules.sql</code> file</li>
                      <li>Run the SQL to create the table and set up the necessary policies</li>
                    </ol>
                    <p>After setting up the database, refresh this page and try scheduling an outfit again.</p>
                  </div>
                </div>
              ) : (
                scheduledOutfits.map((schedule) => (
                  <div key={schedule.id} className="scheduled-outfit-card">
                    <div className="scheduled-outfit-date">
                      <h3>{formatDate(schedule.date)}</h3>
                    </div>
                    <div className="scheduled-outfit-details">
                      <h4>{schedule.outfit?.name}</h4>
                      {schedule.outfit?.occasion && (
                        <p className="outfit-occasion">{schedule.outfit.occasion}</p>
                      )}
                      <div className="scheduled-outfit-preview">
                        {schedule.outfit?.items && getSortedItems(schedule.outfit.items).map((item) => (
                          <img
                            key={item.id}
                            src={item.image_url}
                            alt={item.name || item.category}
                            className="scheduled-outfit-item-image"
                          />
                        ))}
                      </div>
                    </div>
                    <div className="scheduled-outfit-actions">
                      <button
                        onClick={() => handleDeleteSchedule(schedule.id)}
                        className="button button-danger"
                      >
                        Remove
                      </button>
                    </div>
                  </div>
                ))
              )}
            </div>
          )}
        </div>
      )}

      {/* Save Outfit Modal */}
      {showSaveModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2 className="section-title">Save Outfit</h2>
            <div className="form-group">
              <label>Outfit Name:</label>
              <input
                type="text"
                value={outfitName}
                onChange={(e) => setOutfitName(e.target.value)}
                placeholder="Enter a name for your outfit"
                className="form-input"
              />
            </div>
            <div className="form-group">
              <label>Occasion (optional):</label>
              <input
                type="text"
                value={outfitOccasion}
                onChange={(e) => setOutfitOccasion(e.target.value)}
                placeholder="Enter an occasion"
                className="form-input"
              />
            </div>
            <div className="modal-actions">
              <button
                onClick={() => {
                  setShowSaveModal(false);
                  setOutfitName('');
                  setOutfitOccasion('');
                }}
                className="button button-secondary"
              >
                Cancel
              </button>
              <button
                onClick={handleSaveOutfit}
                disabled={!outfitName}
                className="button"
              >
                Save
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Schedule Outfit Modal */}
      {showScheduleModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2 className="section-title">Schedule Outfit</h2>
            <div className="form-group">
              <label>Select Date:</label>
              <input
                type="date"
                value={scheduleDate}
                onChange={(e) => setScheduleDate(e.target.value)}
                className="form-input"
                min={new Date().toISOString().split('T')[0]}
              />
            </div>
            <div className="scheduled-outfit-preview">
              <h3>{selectedOutfitForSchedule?.name}</h3>
              {selectedOutfitForSchedule?.occasion && (
                <p className="outfit-occasion">{selectedOutfitForSchedule.occasion}</p>
              )}
              <div className="outfit-preview-grid">
                {selectedOutfitForSchedule?.items && getSortedItems(selectedOutfitForSchedule.items).map((item) => (
                  <div key={item.id} className="outfit-preview-item">
                    <img
                      src={item.image_url}
                      alt={item.name || item.category}
                      className="outfit-preview-image"
                    />
                    <div className="outfit-preview-details">
                      <h3>{item.name || item.category}</h3>
                      {item.brand && <p className="item-brand">{item.brand}</p>}
                    </div>
                  </div>
                ))}
              </div>
            </div>
            <div className="modal-actions">
              <button
                onClick={() => {
                  setShowScheduleModal(false);
                  setSelectedOutfitForSchedule(null);
                  setScheduleDate('');
                }}
                className="button button-secondary"
              >
                Cancel
              </button>
              <button
                onClick={handleScheduleOutfit}
                disabled={!scheduleDate}
                className="button"
              >
                Schedule
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default OutfitPlanner;
