import React from 'react';
import { format } from 'date-fns';
import '../styles/main.css';

interface OutfitPreviewProps {
  outfit?: {
    id: string;
    name: string;
    occasion?: string | null;
    items: string[];
    created_at: string;
  };
}

export const OutfitPreview: React.FC<OutfitPreviewProps> = ({ outfit }) => {
  if (!outfit) {
    return (
      <div className="outfit-preview">
        <p className="text-center">No outfit selected</p>
      </div>
    );
  }

  return (
    <div className="outfit-preview">
      <div className="outfit-grid">
        <div className="outfit-item">
          <h3 className="outfit-item-title">{outfit.name}</h3>
          {outfit.occasion && <p className="outfit-item-name">{outfit.occasion}</p>}
          <p className="outfit-item-name">Created: {format(new Date(outfit.created_at), 'PPP')}</p>
        </div>
      </div>
    </div>
  );
};
