import React, { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { supabase } from '../utils/supabaseClient';
import { Calendar } from '../components/Calendar';
import { WeatherWidget } from '../components/WeatherWidget';
import { SupabaseTest } from '../components/SupabaseTest';
import '../styles/main.css';

interface User {
  name: string;
  email: string;
}

interface Outfit {
  id: string;
  name: string;
  occasion: string | null;
  items: string[];
  created_at: string;
}

const Dashboard: React.FC = () => {
  const [user, setUser] = useState<User | null>(null);
  const [plannedOutfits, setPlannedOutfits] = useState<Outfit[]>([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      const { data: { user } } = await supabase.auth.getUser();
      if (user) {
        setUser({
          name: user.user_metadata?.full_name || 'User',
          email: user.email || '',
        });
      }
    };

    const fetchPlannedOutfits = async () => {
      try {
        const { data, error } = await supabase
          .from('outfits')
          .select('*')
          .order('created_at', { ascending: true })
          .limit(2);

        if (error) {
          console.error('Error fetching outfits:', error);
          return;
        }

        // Transform the data to match our Outfit interface
        const transformedData = (data || []).map((outfit: any) => ({
          id: outfit.id,
          name: outfit.name,
          occasion: outfit.occasion,
          items: outfit.items || [],
          created_at: outfit.created_at
        }));

        setPlannedOutfits(transformedData);
      } catch (error) {
        console.error('Error fetching outfits:', error);
      }
    };

    fetchUser();
    fetchPlannedOutfits();
  }, []);

  const handleClothingScanner = () => {
    navigate('/scanner');
  };

  const handleLogout = async () => {
    try {
      await supabase.auth.signOut();
      navigate('/');
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <div className="header-content">
          <h1 className="dashboard-title">wrdrb</h1>
          <p className="dashboard-subtitle">
            Welcome, {user?.email ? user.email.split('@')[0] : 'User'}. What are we wearing today?
          </p>
        </div>
        <button onClick={handleLogout} className="btn btn-secondary logout-btn">
          Log Out
        </button>
      </div>

      <div className="dashboard-grid">
        <div className="dashboard-card">
          <h2 className="card-title">Weather</h2>
          <WeatherWidget />
        </div>

        <div className="dashboard-card" style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center' }}>
          <h2 className="card-title">Manage Wardrobe</h2>
          <p className="text-center">Add, edit, or remove items from your wardrobe</p>
          <div className="text-center" style={{ marginTop: '1rem' }}>
            <Link to="/clothing" className="btn btn-primary">
              Manage Wardrobe
            </Link>
          </div>
        </div>

        <div className="dashboard-card" style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center' }}>
          <h2 className="card-title">Plan Outfit</h2>
          <p className="text-center">Create and schedule outfits for upcoming events</p>
          <div className="text-center" style={{ marginTop: '1rem' }}>
            <Link to="/outfit-planner" className="btn btn-secondary">
              Plan Outfit
            </Link>
          </div>
        </div>
      </div>

      <section className="section">
        <SupabaseTest />
      </section>

      <section className="section">
        <h2 className="section-title">Planned Outfits</h2>
        <div className="grid grid-cols-1">
          {plannedOutfits.map((outfit) => (
            <div key={outfit.id} className="outfit-preview">
              <div className="outfit-grid">
                <div className="outfit-item">
                  <h3 className="outfit-item-title">{outfit.name}</h3>
                  {outfit.occasion && <p className="outfit-occasion">{outfit.occasion}</p>}
                  <p className="outfit-item-date">Created: {new Date(outfit.created_at).toLocaleDateString()}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </section>

      <section className="section">
        <h2 className="section-title">Weekly Calendar</h2>
        <Calendar />
      </section>

      <button
        onClick={handleClothingScanner}
        className="button"
        style={{ position: 'fixed', bottom: '1.5rem', right: '1.5rem' }}
      >
        <svg
          className="button-icon"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M3 9a2 2 0 012-2h.93a2 2 0 001.664-.89l.812-1.22A2 2 0 0110.07 4h3.86a2 2 0 011.664.89l.812 1.22A2 2 0 0018.07 7H19a2 2 0 012 2v9a2 2 0 01-2 2H5a2 2 0 01-2-2V9z"
          />
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M15 13a3 3 0 11-6 0 3 3 0 016 0z"
          />
        </svg>
        Scan Clothing
      </button>
    </div>
  );
};

export default Dashboard;
