import React, { useEffect, useState } from 'react';
import { format } from 'date-fns';
import '../styles/main.css';

interface WeatherData {
  temperature: number;
  condition: string;
  icon: string;
}

export const WeatherWidget: React.FC = () => {
  const [weather, setWeather] = useState<WeatherData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        const response = await fetch(
          `https://api.openweathermap.org/data/2.5/weather?q=London&appid=${process.env.REACT_APP_OPENWEATHER_API_KEY}&units=metric`
        );
        const data = await response.json();

        if (!response.ok) {
          throw new Error(data.message || 'Failed to fetch weather data');
        }

        setWeather({
          temperature: Math.round(data.main.temp),
          condition: data.weather[0].main,
          icon: data.weather[0].icon,
        });
      } catch (err) {
        setError(err instanceof Error ? err.message : 'Failed to fetch weather');
      } finally {
        setLoading(false);
      }
    };

    fetchWeather();
  }, []);

  if (loading) {
    return (
      <div className="card">
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '2rem' }}>
          <div className="spinner" style={{ marginRight: '0.5rem' }}></div>
          Loading weather...
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="card">
        <div style={{ color: '#991b1b', padding: '1rem' }}>
          Error: {error}
        </div>
      </div>
    );
  }

  return (
    <div className="card">
      <div className="weather-widget">
        <div className="weather-info">
          <h3 style={{ fontSize: '1.125rem', fontWeight: 600, marginBottom: '0.25rem' }}>
            {format(new Date(), 'EEEE, MMMM d')}
          </h3>
          <p style={{ fontSize: '1.5rem', fontWeight: 700, marginBottom: '0.25rem' }}>
            {weather?.temperature}Â°C
          </p>
          <p style={{ color: '#4b5563', textTransform: 'capitalize' }}>
            {weather?.condition}
          </p>
        </div>
        {weather?.icon && (
          <img
            src={`http://openweathermap.org/img/wn/${weather.icon}@2x.png`}
            alt={weather.condition}
            className="weather-icon"
          />
        )}
      </div>
    </div>
  );
};
